        "基础": [0, "衡量不可知的运气，一种玄学"]
      }
    }
  },
  "skill": {
    "开发技能": {
      "程序设计": [0, "编写、调试和优化代码的专业能力"],
      "美术设计": [0, "创作2D原画、3D建模、UI等视觉元素的能力"],
      "音乐音效": [0, "创作游戏配乐与声音效果的能力"],
      "叙事设计": [0, "构建世界观、撰写剧情与设计任务的能力"],
      "系统策划": [0, "设计核心玩法、数值体系与成长循环的能力"]
    },
    "专业知识": {
      "游戏史": [0, "对本世界主流游戏发展历史与设计的了解程度"],
      "市场营销": [0, "关于产品推广、社区运营与商业化的知识"],
      "项目管理": [0, "规划开发流程、分配资源与控制进度的能力"],
      "法务财经": [0, "了解公司注册、合同、税务等商业运作知识"]
    },
    "生活技能": {
      "烹饪": [0, "为自己准备食物，恢复精力的能力"],
      "驾驶": [0, "驾驶交通工具的能力"],
      "社交": [0, "在日常生活中与人交往，建立友谊的能力"],
      "信息检索": [0, "快速有效地从网络或现实中找到所需信息的能力"]
    }
  }
}

